import React, { useEffect, useState } from 'react';
import { Table, Space, Button, Input, Card, Tag, Image, Popconfirm, message } from 'antd';
import { PlusOutlined, EditOutlined, DeleteOutlined } from '@ant-design/icons';
import { requestDeleteProduct, requestGetAllProduct } from '../../../Config/request';

const ProductManagement = ({ setActiveComponent, setProductId }) => {
    // Mock data - sau này sẽ được thay thế bằng dữ liệu thật từ API

    const columns = [
        {
            title: 'Hình ảnh',
            dataIndex: 'image',
            key: 'image',
            render: (image) => (
                <Image
                    src={image}
                    alt="product"
                    width={80}
                    height={80}
                    fallback="data:image/png;base64,"
                />
            ),
        },
        {
            title: 'Tên sản phẩm',
            dataIndex: 'name',
            key: 'name',
            sorter: (a, b) => a.name.localeCompare(b.name),
        },
        {
            title: 'Giá gốc',
            dataIndex: 'price',
            key: 'price',
            render: (price) => `${price.toLocaleString()} VNĐ`,
            sorter: (a, b) => a.price - b.price,
        },
        {
            title: 'Giá khuyến mãi',
            dataIndex: 'priceDiscount',
            key: 'priceDiscount',
            render: (price) => `${price.toLocaleString()} VNĐ`,
        },
        {
            title: 'Tồn kho',
            dataIndex: 'stock',
            key: 'stock',
            render: (stock) => (
                <Tag color={stock > 0 ? 'green' : 'red'}>{stock > 0 ? `${stock} sản phẩm` : 'Hết hàng'}</Tag>
            ),
        },
        {
            title: 'Thao tác',
            key: 'action',
            render: (_, record) => (
                <Space size="middle">
                    <Button type="primary" icon={<EditOutlined />} onClick={() => handleEdit(record)}>
                        Sửa
                    </Button>
                    <Popconfirm
                        title="Bạn có chắc chắn muốn xóa sản phẩm này?"
                        onConfirm={() => handleDelete(record.key)}
                        okText="Có"
                        cancelText="Không"
                    >
                        <Button type="primary" danger icon={<DeleteOutlined />}>
                            Xóa
                        </Button>
                    </Popconfirm>
                </Space>
            ),
        },
    ];

    // Xử lý các action
    const handleAdd = () => {
        setActiveComponent('add-product'); // Chuyển sang component AddProduct
    };

    const handleEdit = (record) => {
        setProductId(record.id);
        setActiveComponent('edit-product'); // Chuyển sang component AddProduct
    };

    const handleSearch = (value) => {
        console.log('Search:', value);
    };

    const [products, setProducts] = useState([]);
    const fetchData = async () => {
        const res = await requestGetAllProduct();
        setProducts(res.metadata);
    };
    useEffect(() => {
        fetchData();
    }, []);

    const data = products.map((product) => ({
        key: product._id,
        id: product._id,
        name: product.name,
        price: product.price,
        priceDiscount: product.priceDiscount,
        stock: product.stock,
        image: product.images[0],
    }));

    const handleDelete = async (key) => {
        try {
            await requestDeleteProduct(key);
            await fetchData();
            message.success('Xóa sản phẩm thành công');
        } catch (error) {
            message.error('Xóa sản phẩm thất bại');
        }
    };

    return (
        <div>
            <Card title="Quản lý sản phẩm">
                <Space style={{ marginBottom: 16 }}>
                    <Input.Search placeholder="Tìm kiếm sản phẩm" onSearch={handleSearch} style={{ width: 300 }} />
                    <Button type="primary" icon={<PlusOutlined />} onClick={handleAdd}>
                        Thêm sản phẩm
                    </Button>
                </Space>
            </Card>

            <Table
                columns={columns}
                dataSource={data}
                pagination={{
                    total: products.length,
                    pageSize: 10,
                    showSizeChanger: true,
                    showQuickJumper: true,
                }}
            />
        </div>
    );
};

export default ProductManagement;
